const aws = require("aws-sdk");
const iam = new aws.IAM();

const SUCCESS = "SUCCESS";
const FAILED = "FAILED";

// The `send` function code source https://github.com/ispyinternet/cfn-response-promise/blob/master/index.js
// Built-in `cfn-response` modult doesn't work for async handlers.
const send = function (event, context, responseStatus, responseData, physicalResourceId, noEcho) {
    return new Promise((resolve, reject) => {
        var responseBody = JSON.stringify({
            Status: responseStatus,
            Reason: "See the details in CloudWatch Log Stream: " + context.logStreamName,
            PhysicalResourceId: physicalResourceId || context.logStreamName,
            StackId: event.StackId,
            RequestId: event.RequestId,
            LogicalResourceId: event.LogicalResourceId,
            NoEcho: noEcho || false,
            Data: responseData
        });

        console.log("Response body:\n", responseBody);

        var https = require("https");
        var url = require("url");

        var parsedUrl = url.parse(event.ResponseURL);
        var options = {
            hostname: parsedUrl.hostname,
            port: 443,
            path: parsedUrl.path,
            method: "PUT",
            headers: {
                "content-type": "",
                "content-length": responseBody.length
            }
        };

        var request = https.request(options, function (response) {
            console.log("Status code: " + response.statusCode);
            console.log("Status message: " + response.statusMessage);
            resolve(context.done());
        });

        request.on("error", function (error) {
            console.log("send(..) failed executing https.request(..): " + error);
            reject(context.done(error));
        });

        request.write(responseBody);
        request.end();
    })
}

exports.handler = async (event, context) => {
    console.log("Request received:\n" + JSON.stringify(event));

    if (event.RequestType === "Delete") {
        // we do not remove service linked role
        // because it is a special one and it can be used by another resource
        return await send(event, context, SUCCESS, {});
    }

    const {awsServiceName, rolePathPrefix} = event.ResourceProperties;

    try {
        const listRoles = await iam.listRoles({PathPrefix: rolePathPrefix, MaxItems: 1}).promise();
        const serviceLinkedRoleFound = listRoles.Roles.length > 0;

        if (!serviceLinkedRoleFound) {
            console.log(`There is no role with prefix "${rolePathPrefix}" in the account. Creating...`);
            await iam.createServiceLinkedRole({AWSServiceName: awsServiceName}).promise();
        }

        console.log('Success.');

        return await send(event, context, SUCCESS, {});
    } catch (e) {
        console.log(e);

        return await send(event, context, FAILED, {message: "Call failed. See lambda logs for details."});
    }
};
